'use client';

import { Button } from '@/registry/default/ui/button';
import {
  Collapsible,
  CollapsiblePanel,
  CollapsibleTrigger,
} from '@/registry/default/ui/collapsible';
import { ChevronDownIcon } from 'lucide-react';

export function CollapsibleWithIcon() {
  return (
    <Collapsible className='w-64'>
      <CollapsibleTrigger
        render={<Button variant='outline' className='w-full justify-between' />}
      >
        Show more
        <ChevronDownIcon className='size-4 transition-transform duration-200 [[data-panel-open]_&]:rotate-180' />
      </CollapsibleTrigger>
      <CollapsiblePanel className='pt-2'>
        <p className='text-sm text-muted-foreground'>
          The chevron icon rotates when the panel is open.
        </p>
      </CollapsiblePanel>
    </Collapsible>
  );
}
