'use client';

import {
  Combobox,
  ComboboxChip,
  ComboboxChips,
  ComboboxChipsInput,
  ComboboxContent,
  ComboboxEmpty,
  ComboboxItem,
  ComboboxList,
  useComboboxAnchor,
} from '@/registry/default/ui/combobox';

const tags = ['React', 'Vue', 'Angular', 'Svelte', 'Next.js', 'Nuxt'];

export function ComboboxMultiSelect() {
  const anchor = useComboboxAnchor();

  return (
    <Combobox items={tags} multiple>
      <ComboboxChips ref={anchor}>
        {(item) => <ComboboxChip value={item}>{item}</ComboboxChip>}
        <ComboboxChipsInput placeholder='Select tags...' />
      </ComboboxChips>
      <ComboboxContent anchor={anchor.current}>
        <ComboboxEmpty>No tags found.</ComboboxEmpty>
        <ComboboxList>
          {(item) => (
            <ComboboxItem key={item} value={item}>
              {item}
            </ComboboxItem>
          )}
        </ComboboxList>
      </ComboboxContent>
    </Combobox>
  );
}
